mapper.select:
SELECT * FROM ( SELECT rownum as rnum, n.* FROM ( SELECT noticeseq ,notice_title ,userid ,notice_contents ,notice_date FROM notice
WHERE ${searchItem} LIKE '%'||#{searchWord}||'%'
ORDER BY noticeseq DESC ) n ) WHERE rnum between #{srow} and #{erow}

mapper.insert:
INSERT INTO notice
(
	noticeseq
	,notice_title
	,userid
	,notice_contents
	,notice_date
)
VALUES
(
	SEQ_notice_noticeseq.nextval
	,#{notice_title}
	,#{userid}
	,#{notice_contents}
	,#{notice_date}
)

mapper.update:
UPDATE notice
SET
	notice_title = #{notice_title}
	,notice_contents = #{notice_contents}
	,notice_date = #{notice_date}
WHERE
	noticeseq	= #{noticeseq}
